import { REQUEST_LOADING, GET_CHARACTER, ERROR } from "../actions/actions";

const INITIAL_STATE = {
  loading: false,
  name: "",
  gender: "",
  aliases: "",
  books: "",
  error: null,
};

const reducerFetch = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case REQUEST_LOADING:
      return {
        ...state,
        loading: true,
      };
    case GET_CHARACTER:
      return {
        ...state,
        loading: false,
        name: action.resultAPI.name,
        gender: action.resultAPI.gender,
        aliases: action.resultAPI.aliases,
        books: action.resultAPI.books,
        error: null,
      };
    case ERROR:
      return {
        ...state,
        loading: false,
        error: 'Api Falhou'
      }
    default:
      return state;
  }
};

export default reducerFetch;
